mapper.select:
select b.headimgurl,t.user_id, s.name user_name , t.steps from HNJBZ_WALK_DAY t
inner join HNJBZ_CHANNEL_USER b on t.user_id=b.id
inner join HNJBZ_USER_WHITE s on b.binding_phone=s.phone
inner join HNJBZ_ORG m on m.id=s.org_id and m.parent_id = #{ record.companyId}


        and t.step_date = #{ record.stepDate}


        and m.id = #{ record.orgId}


order by t.steps desc

select m.id org_id, m.name org_name ,avg( t.steps ) steps from HNJBZ_WALK_DAY t inner join HNJBZ_CHANNEL_USER b on t.user_id=b.id inner join HNJBZ_USER_WHITE s on b.binding_phone=s.phone inner join HNJBZ_ORG m on m.id=s.org_id and m.parent_id = #{record.companyId}
and t.step_date = #{ record.stepDate}
group by m.id,m.name order by steps desc

select o.id company_id , o.name company_name ,avg( t.steps ) steps from HNJBZ_WALK_DAY t inner join HNJBZ_CHANNEL_USER b on t.user_id=b.id inner join HNJBZ_USER_WHITE s on b.binding_phone=s.phone inner join HNJBZ_ORG m on m.id=s.org_id inner join HNJBZ_ORG o on o.id=m.parent_id and o.parent_id= #{record.branchId}
and t.step_date = #{ record.stepDate}
group by o.id,o.name order by steps desc

select b.headimgurl,t.user_id, s.name user_name , sum( t.steps ) steps from HNJBZ_WALK_DAY t
inner join HNJBZ_CHANNEL_USER b on t.user_id=b.id
inner join HNJBZ_USER_WHITE s on b.binding_phone=s.phone
inner join HNJBZ_ORG m on m.id=s.org_id and m.parent_id = #{record.companyId}


        and t.step_date >= #{record.stepDateBegin}


        and t.step_date <= #{record.stepDateEnd}


        and m.id= #{record.orgId}


group by b.headimgurl,t.user_id, s.name
order by steps desc

select m.id org_id, m.name org_name ,avg( t.steps ) steps from HNJBZ_WALK_DAY t
inner join HNJBZ_CHANNEL_USER b on t.user_id=b.id
inner join HNJBZ_USER_WHITE s on b.binding_phone=s.phone
inner join HNJBZ_ORG m on m.id=s.org_id and m.parent_id = #{record.companyId}


        and t.step_date >= #{record.stepDateBegin}


        and t.step_date <= #{record.stepDateEnd}


group by m.id,m.name
order by steps desc

select o.id company_id , o.name company_name ,avg( t.steps ) steps from HNJBZ_WALK_DAY t
inner join HNJBZ_CHANNEL_USER b on t.user_id=b.id
inner join HNJBZ_USER_WHITE s on b.binding_phone = s.phone
inner join HNJBZ_ORG m on m.id=s.org_id
inner join HNJBZ_ORG o on o.id=m.parent_id and o.parent_id= #{record.branchId}


        and t.step_date >= #{record.stepDateBegin}


        and t.step_date <= #{record.stepDateEnd}


group by o.id,o.name
order by steps desc
